section.sourcecode:
address = element address { postal?, phone?, email*, uri*, # existing subelements continue as defined by RFCXML v3 }
uri = element uri { attribute type { "authorid" | "homepage" | "profile" | "other" }?, attribute scheme { "orcid" | "did" | "isni" | "researcherid" | "scopus" }?, xsd:anyURI }
# Additional semantic constraints (non-normative at this stage): # - Exactly one uri[@type='authorid'] SHOULD appear per author. # - Zero or more uri[@type='homepage'|'profile'|'other'] MAY appear. # - When type='authorid', @scheme MAY indicate the identifier system; the URI itself # SHOULD be canonical for that scheme (e.g., https://orcid.org/NNNN-... or did:... ).

# Illustrative only — not valid RFCXML v3
address = element address {
  postal?, phone?, email*, uri*
}
uri = element uri {
  attribute type { "authorid" | "homepage" | "profile" | "other" }?,
  attribute scheme { "orcid" | "did" | "isni" | "researcherid" | "scopus" }?,
  xsd:anyURI
}

# Illustrative only — not valid RFCXML v3
author = element author {
  (organization?, address?, author-id*, ...)
}
author-id = element author-id {
  attribute type { "orcid" | "did" | "isni" | "researcherid" | "scopus" }?,
  xsd:anyURI
}
